{"parameter_event_param2":{"scriptActive":true,"script":"/*
**Access Flow/Action data using the fd_data object. Script must return a value.
**example: var shortDesc = fd_data.trigger.current.short_description;
**return shortDesc;
*/
var created_by = fd_data.trigger.current.sys_created_by;
var manager = '';
var gr_user = new GlideRecord('sys_user');
gr_user.addQuery("user_name", created_by);
gr_user.setLimit(1);
gr_user.query();
if (gr_user.next()) {
 manager = gr_user.manager.email+'';
}
return manager;
"}}

{"parameter_flow_context_id":{"scriptActive":true,"script":"/*
**Access Flow/Action data using the fd_data object. Script must return a value.
**example: var shortDesc = fd_data.trigger.current.short_description;
**return shortDesc;
*/
//return current.sys_id + '';
var flow_context_id = '';
var current_record_id = fd_data.trigger.current.sys_id+'';
var gr_sys_flow_context = new GlideRecord("sys_flow_context");
gr_sys_flow_context.addEncodedQuery("flow=0ea8ff13c3602010ad755cb981d3ae93^name=Notify Contact Reminder");
gr_sys_flow_context.addQuery("source_record", current_record_id);
gr_sys_flow_context.orderByDesc('sys_created_on');
gr_sys_flow_context.setLimit(1);
gr_sys_flow_context.query();
if (gr_sys_flow_context.next()) {
 flow_context_id = gr_sys_flow_context.sys_id+'';
}
//flow_context_id = FlowScriptAPI.getContextID(fd_data.trigger.current);
return flow_context_id;"}}

